#from __future__ import unicode_literalsfrom django.db import modelsfrom django.conf import settings#画面認証用from django.utils.encoding import python_2_unicode_compatiblefrom django.contrib.auth.models import AbstractBaseUser, BaseUserManager, PermissionsMixinfrom django.core.exceptions import ValidationError  #add240903from django.db.models import Q  #add240918import datetime##from datetime import datetime#変更した場合、テンプレートの検索欄も要変更ACCOUNT_TYPE_CHOICES = (    ('', settings.EMPTY_MARK),    (1, '普通'),    (2, '当座'),)TRADE_DIVISION_CHOICES = (    (0, '外注・工事仕入'),    (1, '経費'),)PAY_DAY_DIVISION_CHOICES = (    (0, '当月'),    (1, '翌月'),    (2, '翌々月'),    (3, '月末(休日前倒し)'),    (4, '月末(休日未考慮)'),    (5, '翌月末(休日前倒し)'),    (6, '翌月末(休日先送り)'),)PAYMENT_METHOD_CHOICES = (    ('', settings.EMPTY_MARK),    (1, '振込'),    (2, '口座振替'),    (3, 'ＡＴＭ'),    (4, '現金'),)FIXED_CONTENT_CHOICES = (    ('', settings.EMPTY_MARK),    (1, '項目のみ'),    (2, '項目のみ（指定月）'),    (3, '項目・固定費'),    (4, '項目・固定費（指定月）'),    (5, '項目・概算＝仕入金額'),)COMPLETED_CHOICES = (    (0, '未払'),    (1, '完了'),)BORROW_LEND_CHOICES = (    (0, '貸'),    (1, '借'),)BANK_CHOICES = (    ('', settings.EMPTY_MARK),    (0, '北越'),    (1, 'さんしん(塚野目)'),    (2, 'さんしん(本店)'),)#消費税フラグ(軽減税率を考慮)REDUCED_TAX_CHOICES = (    ('', settings.EMPTY_MARK),    (1, '８％'),     #増税の際は、ここを変える)# Create your models here.class Bank(models.Model):    """銀行マスター"""    order = models.IntegerField('ソート順', blank=True, default=0)    name = models.CharField('銀行名', max_length=255)    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    update_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        def __str__(self):        return self.nameclass Bank_Branch(models.Model):    """銀行支店マスター"""    order = models.IntegerField('ソート順', blank=True, default=0)    #bank_id = models.IntegerField('銀行ID', blank=True, default=0)    bank = models.ForeignKey(Bank, verbose_name="銀行名", null=True, blank=True, on_delete=models.SET_NULL )    name = models.CharField('銀行支店名', max_length=255)    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    update_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        def __str__(self):        return self.name        #勘定科目(代表者貸借金システム用)class Account(models.Model):    """勘定科目マスター(貸借用)"""    order = models.IntegerField('ソート順', blank=True, default=0)    name = models.CharField('勘定科目名', max_length=255)    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    update_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        def __str__(self):        return self.name#class Sub_Account(models.Model):class Account_Sub(models.Model):    """勘定科目補助マスター(貸借用)"""    order = models.IntegerField('ソート順', blank=True, default=0)    name = models.CharField('補助科目名', max_length=255)    account = models.ForeignKey(Account, verbose_name="勘定科目名", null=True, blank=True, on_delete=models.SET_NULL )    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    update_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        def __str__(self):        return self.name        #勘定科目class Account_Title(models.Model):    """勘定科目マスター"""    order = models.IntegerField('ソート順', blank=True, default=0)    name = models.CharField('勘定科目名', max_length=255)    trade_division_id = models.IntegerField('取引区分(0:経費 1:外注等)', blank=True, choices=TRADE_DIVISION_CHOICES, default=0)    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    update_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        #テンプレートにリストの選択値を表示させるためのもの    def trade_division_verbose(self):        return dict(TRADE_DIVISION_CHOICES)[self.trade_division_id]    def __str__(self):        return self.nameclass Partner(models.Model):    """取引先マスター"""    order = models.IntegerField('ソート順', blank=True, default=0)    administrative_name = models.CharField('取引先名(管理用)', max_length=255, blank=True)    name = models.CharField('取引先名', max_length=255)    trade_division_id = models.IntegerField('取引区分ID', blank=True, choices=TRADE_DIVISION_CHOICES, default=0)    #account_title_id = models.IntegerField('支払科目ID', blank=True, default=0)    account_title = models.ForeignKey(Account_Title, verbose_name="支払科目", null=True, blank=True, on_delete=models.SET_NULL)        payment_method_id = models.IntegerField('支払方法ID', blank=True, null=True, choices=PAYMENT_METHOD_CHOICES)        #振込(振替)元--デフォはホクギンとする。    source_bank = models.ForeignKey(Bank, related_name='source_bank', verbose_name="振込/振替元銀行", null=True, blank=True, default=1, on_delete=models.SET_NULL)    #add200120    #振込(振替)元銀行支店    source_bank_branch = models.ForeignKey(Bank_Branch, related_name='source_bank_branch', verbose_name="振込/振替元銀行支店", null=True, blank=True, on_delete=models.SET_NULL )            bank = models.ForeignKey(Bank, verbose_name="振込先銀行", null=True, blank=True, on_delete=models.SET_NULL)    bank_name = models.CharField('振込先銀行名', max_length=255)   #あとで抹消-->消すとmigrateでエラー出る(200120)    bank_branch = models.ForeignKey(Bank_Branch, verbose_name="振込先支店名", null=True, blank=True, on_delete=models.SET_NULL)    branch_name = models.CharField('振込先支店名', max_length=255)  #あとで抹消-->消すとmigrateでエラー出る(200120)    account_type = models.IntegerField('口座種別', blank=True, null=True, choices=ACCOUNT_TYPE_CHOICES)    account_number = models.CharField('口座番号', default="", blank=True, max_length=16)   #upd180312        pay_day =  models.IntegerField('支払日', blank=True, default=0)    pay_day_division = models.IntegerField('支払フラグ(月末等)', default=0, choices=PAY_DAY_DIVISION_CHOICES )    #支払月フラグ    pay_month_flag_1 = models.BooleanField('１月', default=False)    pay_month_flag_2 = models.BooleanField('２月', default=False)    pay_month_flag_3 = models.BooleanField('３月', default=False)    pay_month_flag_4 = models.BooleanField('４月', default=False)    pay_month_flag_5 = models.BooleanField('５月', default=False)    pay_month_flag_6 = models.BooleanField('６月', default=False)    pay_month_flag_7 = models.BooleanField('７月', default=False)    pay_month_flag_8 = models.BooleanField('８月', default=False)    pay_month_flag_9 = models.BooleanField('９月', default=False)    pay_month_flag_10 = models.BooleanField('１０月', default=False)    pay_month_flag_11 = models.BooleanField('１１月', default=False)    pay_month_flag_12 = models.BooleanField('１２月', default=False)    #    fixed_content_id = models.IntegerField('固定項目フラグ', blank=True, null=True, choices=FIXED_CONTENT_CHOICES )    rough_estimate = models.IntegerField('概算', blank=True, null=True)    fixed_cost = models.IntegerField('固定費', blank=True, null=True)    #        created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    update_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        #テンプレートにリストの選択値を表示させるためのもの    def trade_division_verbose(self):        return dict(TRADE_DIVISION_CHOICES)[self.trade_division_id]        #ex.モデル単位で取得する場合        #return dict(Scoop.FLAVOR_CHOCIES)[self.flavor]    def payment_method_verbose(self):    #支払方法        if self.payment_method_id != None:            return dict(PAYMENT_METHOD_CHOICES)[self.payment_method_id]        else:            return dict(PAYMENT_METHOD_CHOICES)['']    def account_type_verbose(self):    #口座種別        if self.account_type != None:          return dict(ACCOUNT_TYPE_CHOICES)[self.account_type]        else:          return dict(ACCOUNT_TYPE_CHOICES)['']    def __str__(self):        return self.nameclass Payment(models.Model):    """支払ファイル"""    order = models.IntegerField('ソート順', blank=True, default=0)    #billing_year_month = models.CharField('支払年月', max_length=6)    billing_year_month = models.DateField('請求〆年月')    trade_division_id = models.IntegerField('取引区分ID', null=True, blank=True, choices=TRADE_DIVISION_CHOICES)    #partner_id = models.IntegerField('取引先ID', blank=True, default=0)    partner = models.ForeignKey(Partner, verbose_name="取引先", null=True, blank=True, on_delete=models.SET_NULL)    #accounttitle_id = models.IntegerField('科目ID', blank=True, default=0)    account_title = models.ForeignKey(Account_Title, verbose_name="項目", null=True, blank=True, on_delete=models.SET_NULL)    billing_amount = models.IntegerField('請求金額', blank=True, null=True)    rough_estimate = models.IntegerField('概算', blank=True, null=True)        payment_amount = models.IntegerField('支払金額', blank=True, null=True)    commission = models.IntegerField('手数料', blank=True, null=True)        payment_method_id = models.IntegerField('支払方法', blank=True, null=True, choices=PAYMENT_METHOD_CHOICES, default=0)        #振込(振替)元    source_bank = models.ForeignKey(Bank, related_name='payment_source_bank', verbose_name="振込/振替元銀行", null=True, blank=True, on_delete=models.SET_NULL )    #振込(振替)元銀行支店    source_bank_branch = models.ForeignKey(Bank_Branch, related_name='payment_source_bank_branch', verbose_name="銀行支店", null=True, blank=True, on_delete=models.SET_NULL )        payment_due_date = models.DateField('支払予定日',blank=True, null=True)    payment_date = models.DateField('支払日',blank=True, null=True)        unpaid_amount = models.IntegerField('未払金額', blank=True, null=True)    unpaid_due_date = models.DateField('未払支払予定日',blank=True, null=True)  #add230313    unpaid_date = models.DateField('未払支払日',blank=True, null=True)    payment_due_date_changed = models.DateField('支払予定変更日',blank=True, null=True)    #    #add230127    #completed_flag = models.IntegerField('完了フラグ', blank=True, null=True)    completed_flag = models.IntegerField('完了フラグ', choices=COMPLETED_CHOICES, default=0)        #fixed_cost = models.BooleanField('固定費', default=False)    note = models.CharField('備考', max_length=255,blank=True)    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    update_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        #バリデーション    def clean(self):        if self.payment_method_id is None:            raise ValidationError("支払方法を選択して下さい。")                #テンプレートにリストの選択値を表示させるためのもの    def payment_method_verbose(self):        return dict(PAYMENT_METHOD_CHOICES)[self.payment_method_id]class Payment_Reserve(models.Model):    #add191218    """支払予約入力ファイル"""    order = models.IntegerField('ソート順', blank=True, default=0)    billing_year_month = models.DateField('請求〆年月')    trade_division_id = models.IntegerField('取引区分ID', null=True, blank=True, choices=TRADE_DIVISION_CHOICES)    partner = models.ForeignKey(Partner, verbose_name="取引先", null=True, blank=True, on_delete=models.SET_NULL)    account_title = models.ForeignKey(Account_Title, verbose_name="項目", null=True, blank=True, on_delete=models.SET_NULL)    billing_amount = models.IntegerField('請求金額', blank=True, null=True)    rough_estimate = models.IntegerField('概算', blank=True, null=True)        #誤入力防止のため抹消    #payment_amount = models.IntegerField('支払金額', blank=True, null=True)    #commission = models.IntegerField('手数料', blank=True, null=True)        payment_method_id = models.IntegerField('支払方法', blank=True, null=True, choices=PAYMENT_METHOD_CHOICES, default=0)        #振込(振替)元    source_bank = models.ForeignKey(Bank, related_name='payment_reserve_source_bank', verbose_name="振込/振替元銀行", null=True, blank=True, on_delete=models.SET_NULL )        #add200120    #振込(振替)元銀行支店    source_bank_branch = models.ForeignKey(Bank_Branch, related_name='payment_reserve_source_bank_branch', verbose_name="銀行支店", null=True, blank=True, on_delete=models.SET_NULL )            payment_due_date = models.DateField('支払予定日',blank=True, null=True)        #支払日は誤入力を防ぐためにカット    #payment_date = models.DateField('支払日',blank=True, null=True)    #note = models.CharField('備考', max_length=255,blank=True)        unpaid_due_date = models.DateField('未払支払予定日',blank=True, null=True)  #add230313        #add230626    unpaid_amount = models.IntegerField('未払金額', blank=True, null=True)    unpaid_date = models.DateField('未払支払日',blank=True, null=True)    #    #    #add230217    completed_flag = models.IntegerField('完了フラグ', choices=COMPLETED_CHOICES, default=0)        created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    update_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)     #テンプレートにリストの選択値を表示させるためのもの    def payment_method_verbose(self):        return dict(PAYMENT_METHOD_CHOICES)[self.payment_method_id]#代表者貸付金データclass Daily_Representative_Loan(models.Model):    #id = models.AutoField(primary_key=True)    table_type_id = models.IntegerField('テーブル種類ID', null=True, blank=True)    table_id = models.IntegerField('テーブルID', null=True, blank=True)    occurred_on = models.DateField('発生日')    #account_id = models.IntegerField('科目ID', null=True, blank=True)    account = models.ForeignKey(Account, verbose_name="科目", null=True, blank=True, on_delete=models.SET_NULL)    #sub_account_id = models.IntegerField('補助科目ID', null=True, blank=True)    account_sub = models.ForeignKey(Account_Sub, verbose_name="補助", null=True, blank=True, on_delete=models.SET_NULL)    description = models.CharField('摘要', max_length=255)    debit = models.IntegerField('借方金額', null=True, blank=True)    credit = models.IntegerField('貸方金額', null=True, blank=True)    #    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    updated_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        #class Meta:    #    managed = False    #    db_table = 'daily_representative_loans'#貸付金月次残高ファイルclass Monthly_Representative_Loan(models.Model):    """貸付金月次残高ファイル"""    occurred_year_month = models.DateField('発生年月')    last_month_balance = models.IntegerField('前月残', blank=True, null=True)    #    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    updated_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)#240726 作成中...#未払費用データclass Accrued_Expence(models.Model):    """未払費用データ"""    occurred_on = models.DateField('発生日', null=True)    saraly = models.IntegerField('給与', null=True, blank=True)    #    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    updated_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)    #日次入金データ(Rails共用)class Deposit(models.Model):    id = models.AutoField(primary_key=True)    table_type_id = models.IntegerField('テーブル種類ID', null=True, blank=True)    table_id = models.IntegerField('テーブルID', null=True, blank=True)    deposit_due_date = models.DateField('入金予定日')    name = models.CharField('名称', max_length=255)    deposit_amount = models.IntegerField('入金金額', null=True, blank=True)    deposit_source_id = models.IntegerField('入金先ID', null=True, blank=True)    completed_flag = models.IntegerField('完了フラグ', null=True, blank=True)    #    created_at = models.DateTimeField('登録日時',auto_now_add=True, blank=True, null=True)    updated_at = models.DateTimeField('更新日時',auto_now=True, blank=True, null=True)        class Meta:        managed = False        db_table = 'deposits'        #日次出金データ(Rails共用)class Expence(models.Model):    id = models.AutoField(primary_key=True)    table_type_id = models.IntegerField('テーブル種類ID', null=True, blank=True)    table_id = models.IntegerField('テーブルID', null=True, blank=True)    payment_method_id = models.IntegerField('支払方法ID', null=True, blank=True)    payment_on = models.DateField('支払日')    unpaid_on = models.DateField('未払支払日')    name = models.CharField('名称', max_length=255)    payment_amount = models.IntegerField('支払金額', null=True, blank=True)    unpaid_amount = models.IntegerField('未払金額', null=True, blank=True)